ns1:pendingAsset.ns1:body:
# Load Common functions
. .\Logging.ps1
. .\GlobalRepository.ps1
. .\LaunchProcess.ps1
. .\FilesUtility.ps1
. .\PlatformUtils.ps1


# get current script location
$scriptPath = split-path -parent $MyInvocation.MyCommand.Definition
$scriptName = $MyInvocation.MyCommand.Name

ConfigureLogging $scriptPath $scriptName


#########################################################################
# Main
#########################################################################

#Use ArgList variable in Start-Process commands so embedded variables and env variables resolve

$DomainNetBIOS = 			(get-globalvariable("Global\DomainNetBIOS")).value
$EventSubscriptionGroup =	"Avecto-GPO-Event Subscription Computers"		# Security Group for Computer Accounts for the evevnt collector subscription
$SubscriptionXML =			"$env:windir\Debug\Avecto\$env:computername-Avecto-EventCentralisationSubscription.xml"


Log "**** Starting: Avecto Defendpoint Install ****"
Log "`n"


Log "Install Avecto Defendpoint GPMC Snapin..."
If ( Get-ItemProperty HKLM:\Software\Microsoft\Windows\CurrentVersion\Uninstall\* | Select-Object 'DisplayName' | Where-object {$_.DisplayName -Match 'Avecto Defendpoint Consoles'} )
{
	Log "GPMC Snapin already installed"
}
Else
{
	$arglist = "/S /v/qn"
	Start-Process -NoNewWindow -Wait -PassThru -FilePath "$ScriptPath\Defendpoint\Binaries\DefendpointManagementConsoles_x64.exe" -ArgumentList $arglist
	Log "`n"
}


Log "Configuring Event Forwarding pre-requisites WinRM..."
winrm quickconfig /q:true
wecutil qc /q:true
Log "`n"

Function StartSvc ($ServiceName)
{
	$svc = Get-Service | Where-Object {$_.name -eq "$ServiceName"}
    If ($svc.Status -ne "Running")
	{
		Log " - Staring the $ServiceName service"
		Start-Service $ServiceName
		$svc = Get-Service | Where-Object {$_.name -eq "$ServiceName"}
	}
	Else
	{
		Log " - The $ServiceName service is running"
	}
	Log "`n"
}


Log "Ensure WinRM is running..."
StartSvc -ServiceName "WinRM"
Log "Ensure Event Forwarding is running..."
StartSvc -ServiceName "Wecsvc"
Log "`n"


# Installing Defendpoint Agent..."
$DefendpointService = Get-Service | Where-Object {$_.name -eq "Avecto Defendpoint Service"}
If ($DefendpointService -ne $null)
{
	Log " - The Defendpoint Client is already installed"
    Log "`n"
}
Else
{
	Log " - Defendpoint Client is not installed. Installing..."
	# Deteremine which version of the client to install
	$OSArchitecture = Get-WmiObject Win32_OperatingSystem | Select-Object OSArchitecture
	If ($OSArchitecture.OSArchitecture -eq "64-bit")
	{
		CMD /c Start /WAIT MSIEXEC.exe /i "$ScriptPath\Defendpoint\Binaries\DefendpointClient_x64.msi" /qn /norestart /log $env:windir\Debug\Avecto\$env:computername-DefendpointClient_x64.msi.log
		If ($LastExitCode -ne 0 -And $LastExitCode -ne 3010) {Log "ERROR: $LastExitCode Failed to install Defendpoint Client. Exiting..."; EXIT 1}
	}
	Else
	{
		CMD /c Start /WAIT MSIEXEC.exe /i "$ScriptPath\Defendpoint\Binaries\DefendpointClient_x86.msi" /qn /norestart /log $env:windir\Debug\Avecto\$env:computername-DefendpointClient_x86.msi.log
		If ($LastExitCode -ne 0 -And $LastExitCode -ne 3010) {Log "ERROR: $LastExitCode Failed to install Defendpoint Client. Exiting..."; EXIT 1}
	}
    Start-Sleep -Seconds 60
}

Log "Stopping and disabling the Avecto Defendpoint Client service"
If ($DefendpointService.Status -ne "Stopped") {Stop-Service "Avecto Defendpoint Service"}
Set-Service "Avecto Defendpoint Service" -StartupType Disabled
Log "`n"


Log "Auto configure the Event Viewer Subscription..."						# Prereq is Defendpoing Client install and then a reboot
& "$ScriptPath\Defendpoint\Scripts\Avecto-EventFwd_Subscription.ps1" -DomainName $DomainNetBIOS -EventForwardingComputerGroupName "$EventSubscriptionGroup" -XMLfile "$SubscriptionXML"
If (!(Test-Path -Path "$SubscriptionXML")) {Log "ERROR: Failed to create XML file. Exiting..."; EXIT 1}
Log "`n"

Log "Creating the Subscription..."
wecutil cs "$SubscriptionXML"
If ($LastExitCode -eq 80)
{
	Log "Subscription already exists"
    Log "`n"
}
Else
{
	If ($LastExitCode -ne 0) {Log "ERROR: $LastExitCode. Failed to create Subscription. Exiting..."; EXIT 1}
}

Log "Setting the subscription latency"
wecutil ss "Avecto Defendpoint Events" /cm:MinLatency
If ($LastExitCode -ne 0) {Log "ERROR: $LastExitCode. Failed to set subscription latency. Exiting..."; EXIT 1}
Log "`n"


Log "**** Finished: Avecto Defendpoint Install ****"

# Script: Unit Testing # Author: Marina Krynina ################################################################################################# # \USER_PROFILE # \Test # \TestResults # \Logs
try { $scriptPath = $env:USERPROFILE . .\LoggingV2.ps1 $true $scriptPath "Execute-UnitTest-Server.ps1" . .\TestFramework\Execute-UnitTest-Server.ps1 $scriptPath exit 0 } catch { $ex = $_.Exception | format-list | Out-String log "ERROR: Exception occurred `nException Message: $ex"
exit 1 }
