ns1:pendingAsset.ns1:body:
# Support multiple deployment environments
$environment = $ENVIRONMENT

$script = @'
Add-Type -AssemblyName System.Web
function set-globalvariable($key,$val) {
__ENVCODE__
Invoke-WebRequest -Method PUT -Uri http://10.5.4.40:4001/v2/keys/$($key)?value=$([System.Web.HttpUtility]::UrlEncode($($val))) -UseBasicParsing
}

function get-globalvariable($key) {
__ENVCODE__
$resp = Invoke-RestMethod -Method GET -Uri http://10.5.4.40:4001/v2/keys/$key
return $resp.node
}

function get-globalvariable_json($key) {
__ENVCODE__
$resp = Invoke-RestMethod -Method GET -Uri http://10.5.4.40:4001/v2/keys/$key
return ConvertFrom-Json $resp.node.value
}


function connect-deploymentshare{
$share = get-globalvariable 'Global/DeploymentShare'
$username = get-globalvariable 'Global/DeploymentUsername'
$password = get-globalvariable 'Global/DeploymentPassword'

$secure_pwd = convertto-securestring $password.value -asplaintext -force

$dcred = new-object -typename System.Management.Automation.PSCredential `
         -argumentlist $username.value, $secure_pwd
$d=New-PSDrive -Name 'Y' -PSProvider FileSystem -Root $share.value -Credential $dcred -Scope Global -persist 

}

function disconnect-deploymentshare{
$d = Remove-PSDrive -Name 'Y'
}

function get-files($computer){
   connect-deploymentshare
   Copy-Item "y:\Common\*" .\ -Recurse -force -Verbose
    $c = $computer
    if( Test-Path -Path ("y:\" + $c)){
    $p = "y:\" + $c + "\*"
    Copy-Item $p .\ -Recurse -force -Verbose
    }else{
        write-output "computername folder does not exist - $c"
    }
   disconnect-deploymentshare

}

function Get-AppFiles($appname){
   connect-deploymentshare
    $p = "y:\" + $appname + "\*"
    if(Test-Path -Path ("y:\" + $appname)){
        Write-Output "Copying Application files from Y:\$($appname)"
        Copy-Item $p .\ -Recurse -force
    } Else {
        Write-Error "Application folder $appname does not exist" -ErrorAction SilentlyContinue
    }
   disconnect-deploymentshare

}
'@


# Perform a replace on __ENVCODE__ to support multiple AD environments
if($environment -eq $null){
	write-output "Generating script for default environment"
	$script = $script -replace "__ENVCODE__",""
}else{
	write-output "Generating script for [$environment] environment"
	$newstring = $('$key= "[' + $environment + ']/$key"')
    	$script = $script -replace "__ENVCODE__",$newstring
}

$script | out-file "GlobalRepository.ps1"
# Load Common functions
#. .\GlobalRepository.ps1
# download common files and computer specific files
#get-files

# Mandatory heading # Load Common functions . .\Logging.ps1 . .\LaunchProcess.ps1 . .\FilesUtility.ps1 . .\Get-SQLInstance.ps1 . .\Get-IniContent.ps1 . .\GlobalRepository.ps1 . .\VariableUtility.ps1 . .\Install-SQL2012.ps1
# get current script location $scriptPath = split-path -parent $MyInvocation.MyCommand.Definition $scriptName = $MyInvocation.MyCommand.Name ConfigureLogging $scriptPath $scriptName
######################################################################### # Main # - Deploys installation files onto the target server # - Extracts installation files # - Installs SQL 2012 SP1 Std ######################################################################### try { $useHardcodedDefaults = $false # Get admin creadentials if ($useHardcodedDefaults) { $domain = (Get-VariableValue $DOMAIN "dev" $useHardcodedDefaults) $user = (Get-VariableValue $USER "administrator" $useHardcodedDefaults) $password = (Get-VariableValue $PASSWORD "p@ssw0rd" $useHardcodedDefaults)
$configFile = $scriptPath + "\" + (Get-VariableValue $CONFIG_XML "3_SQL2012Std_SharePoint.xml" $useHardcodedDefaults)
$svc_sql = (Get-VariableValue $SVC_SQL "svc_sql" $useHardcodedDefaults) $svc_sql_pwd = (Get-VariableValue $SVC_SQL_PWD "p@ssw0rd" $useHardcodedDefaults) } else { $user = (Get-VariableValue $USER "agilitydeploy" $true) $password = (get-globalvariable("ServiceAccount\$user")).value $domain = (get-globalvariable("Global\DomainNetBIOS")).value
$svc_sql = (Get-VariableValue $SVC_SQL "svc_sql" $true) $svc_sql_pwd = (get-globalvariable("ServiceAccount\$svc_sql")).value }
log "INFO: admin user = $domain\$user" log "INFO: sql user = $domain\$svc_sql"
$configFile = "$scriptPath\$CONFIG_XML" $useDefaultAccts = $USEDEFAULTACCOUNTS $instName = $INSTANCENAME $sqlFeatures = $FEATURES
log "INFO: SQL Variables: `nuseDefaultAccts = $useDefaultAccts; `ninstName = $instName; `nsqlFeatures = $sqlFeatures"
if (ifFileExists( $configFile)) { log "INFO: Found $configFile" } else { throw "ERROR: $configFile does not exist" }
# If a parameter hasn't been set, value from the ini file will be used # If you have multiple ini files configured, ensure INSTANCENAME doesn't contain any value. # This set of parameters will be applied to ALL configuration ini files that are listed in the config xml
$parameters = @{}
if ($useDefaultAccts -eq $true) { $ISSVCACCOUNT = "NT Service\MsDtsServer110";
if (($instName).ToUpper() -eq "MSSQLSERVER") { # Default Instance service accounts $SQLSVCACCOUNT = "NT Service\MSSQLSERVER"; $AGTSVCACCOUNT = "NT Service\SQLSERVERAGENT"; $ASSVCACCOUNT = "NT Service\MSSQLServerOLAPService"; $RSSVCACCOUNT = "NT Service\ReportServer" } else { # Named Instance service accounts $SQLSVCACCOUNT = "NT Service\MSSQL" + '$' + $instName $AGTSVCACCOUNT = "NT Service\SQLAgent" + '$' + $instName $ASSVCACCOUNT = "NT Service\MSOLAP" + '$' + $instName $RSSVCACCOUNT = "NT Service\ReportServer" + '$' + $instName }
$parameters.Add("USEDEFAULTACCOUNTS", $useDefaultAccts) $parameters.Add("DOMAIN", $domain) $parameters.Add("SQLSVCACCOUNT", $SQLSVCACCOUNT) $parameters.Add("AGTSVCACCOUNT", $AGTSVCACCOUNT) $parameters.Add("ISSVCACCOUNT", $ISSVCACCOUNT) $parameters.Add("ASSVCACCOUNT", $ASSVCACCOUNT) $parameters.Add("RSSVCACCOUNT", $RSSVCACCOUNT) $parameters.Add("SQLSYSADMINACCOUNTS", "$domain\$user") $parameters.Add("ASSYSADMINACCOUNTS", "$domain\$user")
if ( !([string]::IsNullOrEmpty($instName ) ) ) { $parameters.Add("INSTANCENAME", $instName) $parameters.Add("INSTANCEID", $($instName).ToUpper()) $parameters.Add("FEATURES", $sqlFeatures) # "PID" = ""; #$LicenseKey is stored in the config.xml } } else { $parameters.Add("USEDEFAULTACCOUNTS", $useDefaultAccts) $parameters.Add("DOMAIN", $domain) $parameters.Add("SQLSVCACCOUNT", "$domain\$svc_sql") $parameters.Add("AGTSVCACCOUNT", "$domain\$svc_sql") $parameters.Add("ISSVCACCOUNT", "$domain\$svc_sql") $parameters.Add("ASSVCACCOUNT", "$domain\$svc_sql") $parameters.Add("RSSVCACCOUNT", "$domain\$svc_sql") $parameters.Add("SQLSVCPASSWORD", $svc_sql_pwd) $parameters.Add("AGTSVCPASSWORD", $svc_sql_pwd) $parameters.Add("ISSVCPASSWORD", $svc_sql_pwd) $parameters.Add("ASSVCPASSWORD", $svc_sql_pwd) $parameters.Add("RSSVCPASSWORD", $svc_sql_pwd) $parameters.Add("SQLSYSADMINACCOUNTS", "$domain\$user") $parameters.Add("ASSYSADMINACCOUNTS", "$domain\$user")
if ( !([string]::IsNullOrEmpty($instName ) ) ) { $parameters.Add("INSTANCENAME", $instName) $parameters.Add("INSTANCEID", $($instName).ToUpper()) $parameters.Add("FEATURES", $sqlFeatures) # "PID" = ""; #$LicenseKey is stored in the config.xml } }
$secure_pwd = convertto-securestring $password -asplaintext -force $dcred = new-object -typename System.Management.Automation.PSCredential -argumentlist "$domain\$user", $secure_pwd
$msg = "Installing SQL 2012 binaries" if ((IfFileExists $configFile) -eq $true) {
# Parse configuration xml and execute configured installs log "INFO: Updating configuration file $configFile" UpdateSQLConfigurationFiles $configFile $parameters
log "INFO: Starting $msg" # Execute configured installs log "INFO: Executing configuration files" ExecuteSQLConfigurationFiles $scriptPath $configFile $dcred $parameters }
log "INFO: Finished $msg. `nExit code $result" Write-Host "Script logs: $scriptPath\Logs" Write-Host "SQL Installation logs: C:\Program Files\Microsoft SQL Server\110\Setup Bootstrap\Log"
exit 0 } catch { $ex = $_.Exception | format-list | Out-String log "ERROR: Exception occurred `nException Message: $ex"
exit 1 }
